import uuidfrom sqlalchemy import ForeignKeyfrom sqlalchemy.orm import Mapped, mapped_columnfrom src.db_schemas import Baseclass DbTag(Base):    __tablename__ = 'tag'    id: Mapped[uuid.UUID] = mapped_column(primary_key=True)    latitude: Mapped[float]    longitude: Mapped[float]    description: Mapped[str]    image: Mapped[str | None]    user: Mapped[uuid.UUID] = mapped_column(ForeignKey('user.id'), nullable=True)class DbLike(Base):    __tablename__ = 'like'    id: Mapped[uuid.UUID] = mapped_column(        primary_key=True,        default=uuid.uuid4    )    tag: Mapped[uuid.UUID] = mapped_column(ForeignKey('tag.id', ondelete='cascade'))    user: Mapped[uuid.UUID] = mapped_column(ForeignKey('user.id'))